import PropTypes from "prop-types";
import Button from "./Button";
const Header = ({ title,onAdd,showAdd }) => {

  return (
    <header className="header">
      <h1> {title} </h1>
      <Button color={showAdd ? "green" : "red"} text={showAdd ? 'Close' : 'Add'} onClick={onAdd}  />
    </header>
  );
};

